import React, { createContext, useContext, useEffect, useState } from 'react'
const ThemeContent = createContext();
const { Provider } = ThemeContent;

function Layout({ children }) {
    return (
        <div style={{ display: 'inline' }}>
            {children}
        </div>
    )
}

function TimeAmount() {
    const count = useContext(ThemeContent)
    return (
        <div style={{
            backgroundColor: 'gray',
            width: 100,
            height: 100,
            justifyContent: 'center',
            display: 'flex',
            alignItems: 'center'
        }}>
            {count}
        </div>
    )
}
function EffectArea() {
    const [timeControl, setControl] = useState(false);
    const [count, set] = useState(0)
    useEffect(() => {
        var timmer = 0;
        if (timeControl) {
            timmer = setInterval(() => set(pre => pre = pre + 1), 1000)
        } else {
            clearInterval(timmer)
            timmer = count;
        }
    }, [timeControl])
    return (
        <Provider value={count}>
            <TimeAmount />
            <div style={{ backgroundColor: 'black', width: 600, height: 600 }}>
                <button onClick={() => setControl(true)}>start</button>
                <button onClick={() => setControl(false)}>stop</button>
            </div>
        </Provider>
    )
}
export default function Timout() {

    return (
        <Layout >
            <EffectArea />
        </Layout>
    )
}
