import React, { useReducer, createContext, useContext } from 'react'
const ThemeContent = createContext();
const { Provider } = ThemeContent;
const initialState = 0
const reducer = (state, aciton) => {
    const Timmer = setTimeout(() => state + 1, 1000);
    switch (aciton) {
        case 'start':
            return Timmer;
        case 'stop':
            return clearTimeout(Timmer);
        default:
            return state
    }
}
function Layout({ children }) {
    return (
        <div style={{ display: 'inline' }}>
            {children}
        </div>
    )
}

function TimeAmount() {
    const count = useContext(ThemeContent)
    return (
        <div style={{
            backgroundColor: 'gray',
            width: 100,
            height: 100,
            justifyContent: 'center',
            display: 'flex',
            alignItems: 'center'
        }}>
            {count}
        </div>
    )
}
function EffectArea() {
    const [count, dispatch] = useReducer(reducer, initialState)
    return (
        <Provider value={count}>
            <TimeAmount />
            <div style={{ backgroundColor: 'black', width: 600, height: 600 }}>
                <button onClick={() => { dispatch('start') }}>start</button>
                <button onClick={() => { dispatch('stop') }}>stop</button>
            </div>
        </Provider>
    )
}
export default function Timout() {

    return (
        <Layout >
            <EffectArea />
        </Layout>
    )
}
