import React, { createContext, useContext, useEffect, useState } from 'react'
const ThemeContent = createContext();
const { Provider } = ThemeContent;

function Layout({ children }) {
    return (
        <div style={{ display: 'inline' }}>
            {children}
        </div>
    )
}

function TimeAmount() {
    const count = useContext(ThemeContent)
    let min
    let sec;
    if (count < 60) {
        sec = count
    } else {
        min = Math.floor(sec / 60)
        sec = Math.floor(sec % 60)
    }
    return (
        <div style={{
            backgroundColor: 'gray',
            width: 100,
            height: 100,
            justifyContent: 'center',
            display: 'flex',
            alignItems: 'center'
        }}>
            {`${min}:${sec}`}
        </div>
    )
}
function EffectArea() {
    const [show, setShow] = useState(false);
    const [count, set] = useState(0)
    const [foo, setFoo] = useState();
    const [store, setStore] = useState([]);
    return (
        <Provider value={count}>
            {show && <TimeAmount />}
            <div style={{ backgroundColor: 'black', width: 600, height: 600 }}>
                <button onClick={() => setShow(pre => !pre)}>show</button>
                {
                    show && <>
                        <button onClick={() => setFoo(setInterval(() => set(pre => pre + 1), 1000))}>start</button>
                        <button onClick={() => setStore(pre => [...pre, count])}>store</button>
                        <button onClick={() => clearInterval(foo)}>stop</button>
                        <button onClick={() => set(0)}>clear</button>
                    </>
                }

                {store.map(data => {
                    return (
                        <div>
                            {data}
                        </div>
                    )
                })}
            </div>
        </Provider >
    )
}
export default function Timout() {

    return (
        <Layout >
            <EffectArea />
        </Layout>
    )
}
